var Themeparks = require("./lib/");
var S = new Themeparks.Parks.DisneylandResortCaliforniaAdventure();


S.GetFacilitiesData().then((data) => {
    for (var k in data) {
        console.log(`${data[k].name} (${k}) :: fastPass: ${data[k].fastPass}`);
        //console.log(JSON.stringify(data[k].locations, null, 2));
        for (var j = 0, loc; loc = data[k].locations[j++];) {
            console.log(`   ${loc.name}: ${loc.location.toString()}`);
        }
    }
});

